import React, { useState } from 'react';
import { PersonForm } from '../components/forms/PersonForm';
import { PensionerForm } from '../components/forms/PensionerForm';
import { PartnerForm } from '../components/forms/PartnerForm';

const TestForms = () => {
  const [activeForm, setActiveForm] = useState('person');

  const handleSubmit = (formData) => {
    // For testing purposes, we'll just log the form data
    console.log('Form submitted:', formData);
    alert('Form data logged to console!');
  };

  const formStyles = {
    container: {
      padding: '20px',
      maxWidth: '1200px',
      margin: '0 auto',
    },
    buttonContainer: {
      display: 'flex',
      justifyContent: 'center',
      gap: '10px',
      marginBottom: '20px',
      position: 'fixed',
      top: '20px',
      left: '50%',
      transform: 'translateX(-50%)',
      zIndex: 1000,
      backgroundColor: 'rgba(9, 76, 59, 0.9)',
      padding: '10px',
      borderRadius: '10px',
    },
    button: {
      padding: '10px 20px',
      backgroundColor: '#094c3b',
      color: 'white',
      border: '2px solid #9ed7cc',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '16px',
    },
    activeButton: {
      backgroundColor: '#9ed7cc',
      color: '#094c3b',
    },
  };

  const renderForm = () => {
    switch (activeForm) {
      case 'person':
        return <PersonForm onSubmit={handleSubmit} />;
      case 'pensioner':
        return <PensionerForm onSubmit={handleSubmit} />;
      case 'partner':
        return <PartnerForm onSubmit={handleSubmit} />;
      default:
        return <PersonForm onSubmit={handleSubmit} />;
    }
  };

  return (
    <div style={formStyles.container}>
      <div style={formStyles.buttonContainer}>
        <button
          style={{
            ...formStyles.button,
            ...(activeForm === 'person' ? formStyles.activeButton : {}),
          }}
          onClick={() => setActiveForm('person')}
        >
          Person Form
        </button>
        <button
          style={{
            ...formStyles.button,
            ...(activeForm === 'pensioner' ? formStyles.activeButton : {}),
          }}
          onClick={() => setActiveForm('pensioner')}
        >
          Pensioner Form
        </button>
        <button
          style={{
            ...formStyles.button,
            ...(activeForm === 'partner' ? formStyles.activeButton : {}),
          }}
          onClick={() => setActiveForm('partner')}
        >
          Partner Form
        </button>
      </div>
      {renderForm()}
    </div>
  );
};

export default TestForms;
