                      ))}
                    </select>
                  </div>
                  <div className="col-md-6">
                    <label htmlFor="priority" className="form-label">
                      Priorité
                    </label>
                    <select
                      className="form-select"
                      id="priority"
                      value={newMessage.priority}
                      onChange={(e) =>
                        setNewMessage({
                          ...newMessage,
                          priority: e.target.value,
                        })
                      }
                    >
                      <option value="low">Basse</option>
                      <option value="medium">Moyenne</option>
                      <option value="high">Haute</option>
                    </select>
                  </div>
                </div>

                <div className="row mb-3">
                  <div className="col-md-6">
                    <label htmlFor="category" className="form-label">
                      Catégorie
                    </label>
                    <select
                      className="form-select"
                      id="category"
                      value={newMessage.category}
                      onChange={(e) =>
                        setNewMessage({
                          ...newMessage,
                          category: e.target.value,
                        })
                      }
                    >
                      <option value="general">Général</option>
                      <option value="report">Rapport</option>
                      <option value="budget">Budget</option>
                      <option value="security">Sécurité</option>
                      <option value="infrastructure">Infrastructure</option>
                      <option value="training">Formation</option>
                      <option value="transfer">Transfert</option>
                    </select>
                  </div>
                </div>

                <div className="mb-3">
                  <label htmlFor="subject" className="form-label">
                    Sujet *
                  </label>
                  <input
                    type="text"
                    className="form-control"
                    id="subject"
                    value={newMessage.subject}
                    onChange={(e) =>
                      setNewMessage({ ...newMessage, subject: e.target.value })
                    }
                    placeholder="Entrer le sujet du message"
                    required
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="message" className="form-label">
                    Message *
                  </label>
                  <textarea
                    className="form-control"
                    id="message"
                    rows="6"
                    value={newMessage.message}
                    onChange={(e) =>
                      setNewMessage({ ...newMessage, message: e.target.value })
                    }
                    placeholder="Entrer le contenu du message"
                    required
                  ></textarea>
                </div>

                <div className="mb-3">
                  <label htmlFor="attachment" className="form-label">
                    Pièces jointes
                  </label>
                  <input
                    type="file"
                    className="form-control"
                    id="attachment"
                    multiple
                  />
                  <div className="form-text">
                    Vous pouvez joindre plusieurs fichiers
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-outline-secondary"
                  onClick={() => setShowNewMessageModal(false)}
                >
                  Annuler
                </button>
                <button
                  type="button"
                  className="btn btn-outline-primary"
                  onClick={() => {
                    // Save as draft logic
                    alert("Message sauvegardé en brouillon");
                    setShowNewMessageModal(false);
                  }}
                >
                  Sauvegarder en brouillon
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={handleSendMessage}
                  disabled={
                    !newMessage.province ||
                    !newMessage.subject ||
                    !newMessage.message
                  }
                >
                  <i className="bi bi-send me-2"></i>Envoyer
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ContactProvince;
