import React, { useState } from 'react';
import '../style.css';
import 'bootstrap-icons/font/bootstrap-icons.css';

export const PersonForm = ({ initialData, onSubmit, formType = "create" }) => {
  const [formData, setFormData] = useState(initialData || {
    login: '',
    password: '',
    nom: '',
    prenom: '',
    genre: '',
    role: ''
  });

  const handleChange = (e) => {
    const { id, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [id]: value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <div className="authontification">
      <div className="auth-card">
        <h1 className="login-title">
          {formType === "create" ? "Add Person" : "Edit Person"}
        </h1>

        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label className="input-label" htmlFor="login">Login</label>
            <div className="input-field">
              <i className="bi bi-person input-icon"/>
              <input
                type="text"
                id="login"
                value={formData.login}
                onChange={handleChange}
                placeholder="Enter login"
              />
            </div>
          </div>

          <div className="input-group">
            <label className="input-label" htmlFor="password">Password</label>
            <div className="input-field">
              <i className="bi bi-lock input-icon"/>
              <input
                type="password"
                id="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter password"
              />
            </div>
          </div>

          <div className="input-group">
            <label className="input-label" htmlFor="nom">Last Name</label>
            <div className="input-field">
              <i className="bi bi-person-vcard input-icon"/>
              <input
                type="text"
                id="nom"
                value={formData.nom}
                onChange={handleChange}
                placeholder="Enter last name"
              />
            </div>
          </div>

          <div className="input-group">
            <label className="input-label" htmlFor="prenom">First Name</label>
            <div className="input-field">
              <i className="bi bi-person-vcard-fill input-icon"/>
              <input
                type="text"
                id="prenom"
                value={formData.prenom}
                onChange={handleChange}
                placeholder="Enter first name"
              />
            </div>
          </div>

          <div className="input-group">
            <label className="input-label" htmlFor="genre">Gender</label>
            <div className="input-field">
              <i className="bi bi-gender-ambiguous input-icon"/>
              <select
                id="genre"
                value={formData.genre}
                onChange={handleChange}
                className="form-select"
              >
                <option value="">Select gender</option>
                <option value="male">Male</option>
                <option value="female">Female</option>
                <option value="other">Other</option>
              </select>
            </div>
          </div>

          <div className="input-group">
            <label className="input-label" htmlFor="role">Role</label>
            <div className="input-field">
              <i className="bi bi-person-badge input-icon"/>
              <input
                type="text"
                id="role"
                value={formData.role}
                onChange={handleChange}
                placeholder="Enter role"
              />
            </div>
          </div>

          <button type="submit" className="login-button">
            {formType === "create" ? "Add Person" : "Update Person"}
          </button>
        </form>
      </div>
    </div>
  );
};
